}
private void backtracking(TreeNode node, int target, ArrayList&lt;Integer&gt; path) {
    if (node == null)
        return;
    path.add(node.val);
    target -= node.val;
    if (target == 0 &amp;&amp; node.left == null &amp;&amp; node.right == null) {
        ret.add(new ArrayList&lt;&gt;(path));
