      <pre>public int[] reOrderArray(int[] nums) {
    int N = nums.length;
    for (int i = N - 1; i &gt; 0; i--) {
        for (int j = 0; j &lt; i; j++) {
            if (isEven(nums[j]) &amp;&amp; !isEven(nums[j + 1])) {
                swap(nums, j, j + 1);
            }
        }
    }
    return nums;
}
private boolean isEven(int x) {
    return x % 2 == 0;
}
private void swap(int[] nums, int i, int j) {
    int t = nums[i];
    nums[i] = nums[j];
    nums[j] = t;
}</pre>
