private void mergeSort(int[] nums, int l, int h) {
    if (h - l &lt; 1)
        return;
    int m = l + (h - l) / 2;
    mergeSort(nums, l, m);
    mergeSort(nums, m + 1, h);
    merge(nums, l, m, h);
}
private void merge(int[] nums, int l, int m, int h) {
    int i = l, j = m + 1, k = l;
    while (i &lt;= m || j &lt;= h) {
        if (i &gt; m)
            tmp[k] = nums[j++];
        else if (j &gt; h)
            tmp[k] = nums[i++];
        else if (nums[i] &lt;= nums[j])
            tmp[k] = nums[i++];
        else {
            tmp[k] = nums[j++];
            this.cnt += m - i + 1;  // nums[i] &gt; nums[j]ï¼Œè¯´æ˜ nums[i...mid] éƒ½å¤§äº nums[j]
        }
        k++;
    }
    for (k = l; k &lt;= h; k++)
        nums[k] = tmp[k];
}</pre>
