      <pre>public int[] FindNumsAppearOnce (int[] nums) {
    int[] res = new int[2];
    int diff = 0;
    for (int num : nums)
        diff ^= num;
    diff &amp;= -diff;
    for (int num : nums) {
        if ((num &amp; diff) == 0)
            res[0] ^= num;
        else
            res[1] ^= num;
    }
    if (res[0] &gt; res[1]) {
        swap(res);
    }
    return res;
}
private void swap(int[] nums) {
    int t = nums[0];
    nums[0] = nums[1];
    nums[1] = t;
}</pre>
