    // ç»Ÿè®¡ç™å­æ•°é‡
    int cnt = 0;
    for (int num : nums)
        if (num == 0)
            cnt++;
    // ä½¿ç”¨ç™å­å»è¡¥å…¨ä¸è¿ç»­çš„é¡ºå­
    for (int i = cnt; i &lt; nums.length - 1; i++) {
        if (nums[i + 1] == nums[i])
