    <pre><code class="language-plaintext">public int numSquares(int n) {
    List&lt;Integer&gt; squares = generateSquares(n);
    Queue&lt;Integer&gt; queue = new LinkedList&lt;&gt;();
    boolean[] marked = new boolean[n + 1];
    queue.add(n);
    marked[n] = true;
    int level = 0;
    while (!queue.isEmpty()) {
        int size = queue.size();
        level++;
        while (size-- &gt; 0) {
            int cur = queue.poll();
            for (int s : squares) {
                int next = cur - s;
                if (next &lt; 0) {
                    break;
                }
                if (next == 0) {
                    return level;
                }
                if (marked[next]) {
                    continue;
                }
                marked[next] = true;
                queue.add(next);
            }
        }
    }
    return n;
}
/**
 * ç”Ÿæˆå°äº n çš„å¹³æ–¹æ•°åºåˆ—
 * @return 1,4,9,...
 */
private List&lt;Integer&gt; generateSquares(int n) {
    List&lt;Integer&gt; squares = new ArrayList&lt;&gt;();
    int square = 1;
    int diff = 3;
    while (square &lt;= n) {
        squares.add(square);
        square += diff;
        diff += 2;
    }
    return squares;
}</code></pre>
