    canReach[r][c] = true;
    for (int[] d : direction) {
        int nextR = d[0] + r;
        int nextC = d[1] + c;
        if (nextR &lt; 0 || nextR &gt;= m || nextC &lt; 0 || nextC &gt;= n
                || matrix[r][c] &gt; matrix[nextR][nextC]) {
            continue;
        }
        dfs(nextR, nextC, canReach);
    }
