    int l = 0, h = len;
    while (l &lt; h) {
        int mid = l + (h - l) / 2;
        if (tails[mid] == key) {
            return mid;
        } else if (tails[mid] &gt; key) {
            h = mid;
        } else {
            l = mid + 1;
        }
    }
    return l;
