    <pre>public boolean canFinish(int numCourses, int[][] prerequisites) {
    List&lt;Integer&gt;[] graphic = new List[numCourses];
    for (int i = 0; i &lt; numCourses; i++) {
        graphic[i] = new ArrayList&lt;&gt;();
