    <pre>private int partition(T[] nums, int l, int h) {
    int i = l, j = h + 1;
    T v = nums[l];
    while (true) {
        while (less(nums[++i], v) &amp;&amp; i != h) ;
        while (less(v, nums[--j]) &amp;&amp; j != l) ;
        if (i &gt;= j)
            break;
        swap(nums, i, j);
    }
    swap(nums, l, j);
    return j;
}</pre>
