    <pre>public class QuickFindUF extends UF {
    public QuickFindUF(int N) {
        super(N);
    }
    @Override
    public int find(int p) {
        return id[p];
    }
    @Override
    public void union(int p, int q) {
        int pID = find(p);
        int qID = find(q);
        if (pID == qID) {
