    <pre>public class WeightedQuickUnionUF extends UF {
    // ä¿å­˜èŠ‚ç‚¹çš„æ•°é‡ä¿¡æ¯
    private int[] sz;
    public WeightedQuickUnionUF(int N) {
        super(N);
        this.sz = new int[N];
        for (int i = 0; i &lt; N; i++) {
            this.sz[i] = 1;
        }
    }
    @Override
    public int find(int p) {
        while (p != id[p]) {
            p = id[p];
        }
        return p;
    }
    @Override
    public void union(int p, int q) {
        int i = find(p);
        int j = find(q);
        if (i == j) return;
        if (sz[i] &lt; sz[j]) {
            id[i] = j;
            sz[j] += sz[i];
        } else {
            id[j] = i;
            sz[i] += sz[j];
        }
    }
}</pre>
