    <pre>public class ArrayStack&lt;Item&gt; implements MyStack&lt;Item&gt; {
    // æ ˆå…ƒç´ æ•°ç»„ï¼Œåªèƒ½é€šè¿‡è½¬å‹æ¥åˆ›å»ºæ³›å‹æ•°ç»„
    private Item[] a = (Item[]) new Object[1];
    // å…ƒç´ æ•°é‡
    private int N = 0;
    @Override
    public MyStack&lt;Item&gt; push(Item item) {
        check();
        a[N++] = item;
        return this;
    }
    @Override
    public Item pop() throws Exception {
        if (isEmpty()) {
            throw new Exception(&quot;stack is empty&quot;);
        }
        Item item = a[--N];
        check();
        // é¿å…å¯¹è±¡æ¸¸ç¦»
        a[N] = null;
        return item;
    }
    private void check() {
        if (N &gt;= a.length) {
            resize(2 * a.length);
        } else if (N &gt; 0 &amp;&amp; N &lt;= a.length / 4) {
            resize(a.length / 2);
