    /**
     * è°ƒæ•´æ•°ç»„å¤§å°ï¼Œä½¿å¾—æ ˆå…·æœ‰ä¼¸ç¼©æ€§
     */
    private void resize(int size) {
        Item[] tmp = (Item[]) new Object[size];
        for (int i = 0; i &lt; N; i++) {
            tmp[i] = a[i];
        }
        a = tmp;
    }
    @Override
    public boolean isEmpty() {
        return N == 0;
