      äºŒåˆ†æŸ¥æ‰¾æœ€å¤šéœ€è¦ logN+1 æ¬¡æ¯”è¾ƒï¼Œä½¿ç”¨äºŒåˆ†æŸ¥æ‰¾å®ç°çš„ç¬¦å·è¡¨çš„æŸ¥æ‰¾æ“ä½œæ‰€éœ€è¦çš„æ—¶é—´æœ€å¤šæ˜¯å¯¹æ•°çº§åˆ«çš„ã€‚ä½†æ˜¯æ’å…¥æ“ä½œéœ€è¦ç§»åŠ¨æ•°ç»„å…ƒç´ ï¼Œæ˜¯çº¿æ€§çº§åˆ«çš„ã€‚
    </p>
    <pre>public class BinarySearchOrderedST&lt;Key extends Comparable&lt;Key&gt;, Value&gt; implements OrderedST&lt;Key, Value&gt; {
    private Key[] keys;
    private Value[] values;
    private int N = 0;
    public BinarySearchOrderedST(int capacity) {
        keys = (Key[]) new Comparable[capacity];
        values = (Value[]) new Object[capacity];
    }
    @Override
    public int size() {
        return N;
    }
    @Override
    public int rank(Key key) {
        int l = 0, h = N - 1;
        while (l &lt;= h) {
            int m = l + (h - l) / 2;
            int cmp = key.compareTo(keys[m]);
            if (cmp == 0)
                return m;
            else if (cmp &lt; 0)
                h = m - 1;
            else
                l = m + 1;
        }
        return l;
    }
    @Override
    public List&lt;Key&gt; keys(Key l, Key h) {
        int index = rank(l);
        List&lt;Key&gt; list = new ArrayList&lt;&gt;();
        while (keys[index].compareTo(h) &lt;= 0) {
            list.add(keys[index]);
            index++;
        }
        return list;
    }
    @Override
    public void put(Key key, Value value) {
        int index = rank(key);
        // å¦‚æœæ‰¾åˆ°å·²ç»å­˜åœ¨çš„èŠ‚ç‚¹é”®ä¸º keyï¼Œå°±æ›´æ–°è¿™ä¸ªèŠ‚ç‚¹çš„å€¼ä¸º value
        if (index &lt; N &amp;&amp; keys[index].compareTo(key) == 0) {
            values[index] = value;
            return;
        }
        // å¦åˆ™åœ¨æ•°ç»„ä¸­æ’å…¥æ–°çš„èŠ‚ç‚¹ï¼Œéœ€è¦å…ˆå°†æ’å…¥ä½ç½®ä¹‹åçš„å…ƒç´ éƒ½å‘åç§»åŠ¨ä¸€ä¸ªä½ç½®
        for (int j = N; j &gt; index; j--) {
            keys[j] = keys[j - 1];
            values[j] = values[j - 1];
        }
        keys[index] = key;
        values[index] = value;
        N++;
    }
    @Override
    public Value get(Key key) {
        int index = rank(key);
        if (index &lt; N &amp;&amp; keys[index].compareTo(key) == 0)
            return values[index];
        return null;
    }
    @Override
    public Key min() {
        return keys[0];
    }
    @Override
    public Key max() {
        return keys[N - 1];
    }
}</pre>
