</richcontent>
<richcontent TYPE="NOTE">
<html>
  <head>
    
  </head>
  <body>
    <table class="ne-table">
      <tr>
        <td width="240">
          <p class="ne-p">
            <span class="ne-text">类型</span>
          </p>
        </td>
        <td width="177">
          <p class="ne-p">
            <span class="ne-text">值</span>
          </p>
        </td>
        <td width="284">
          <p class="ne-p">
            <span class="ne-text">备注</span>
          </p>
        </td>
      </tr>
      <tr>
        <td width="240">
          <p class="ne-p">
            <span class="ne-text">bool 布尔</span>
